import { useState } from "react";

const Tooltip = () => {
  const [showTooltip, setShowTooltip] = useState(false);

  const handleClick = () => {
    setShowTooltip(true);
  };

  return (
    <div class>
      <button onClick={handleClick}>Show text </button>
      {showTooltip && (
        <div style={{ border: "1px solid black", padding: "10px" }}>
          <p>Hello World!</p>
        </div>
      )}
    </div>
  );
};

export default Tooltip;
